def ferma(a, x, p):
    final = []
    bin_x = list(bin(x)[2:])
    data = [a]

    for i in range(len(bin_x)-1):
        data.append((data[i] ** 2) % p)
    data.reverse()

    for i in range(len(bin_x)):
        if bin_x[i] == '1':
            final.append(data[i])

    first = final[0]
    for i in range(len(final)-1):
        first = (first*final[i+1]) % p
    return first








def gcd(a, b):
    if a == 0:
        return b
    elif b == 0:
        return a
    elif a >= b:
        return gcd(a % b, b)
    elif a <= b:
        return gcd(a, b % a)

def gcdex(a, b):
    if b == 0:
        return a, 1, 0
    else:
        d, x, y = gcdex(b, a % b)
        return d, y, x - y * (a // b)




